// app.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <h1>Angular Counter App</h1>
    <app-counter [counterValue]="counter" (counterChange)="updateCounter($event)"></app-counter>
  `,
  styles: []
})
export class AppComponent {
  counter: number = 0;

  updateCounter(value: number) {
    this.counter = value;
  }
}
