// app.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <app-card
      [title]="'Card Title'"
      [subtitle]="'Card Subtitle'"
      [description]="'This is a simple card component using Bootstrap in Angular.'"
      [link]="'https://example.com'"
      [linkText]="'Go to example.com'"
    ></app-card>
  `,
  styles: []
})
export class AppComponent {}
